import React from "react";
import axios from "axios";
import { Container, Col } from "shards-react";
import { withRouter } from "react-router";
import Index from "../components/keluargas/index";
import Show from "../components/keluargas/show";
import Form from "../components/anggota-keluarga/form";
import { ThemeConsumer } from "styled-components";

class UpdateGroup extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: 0,
        }
    }

    componentDidMount() {
        this.getFamilies().then( (families) =>{
            console.log(families);



            families.map((family, i)=>{
                console.log(family);
                let uid = family.uid;
                let uid_arr = uid.split('001-002-0021');
                let keluarga_new_uid = '001-002-021'+uid_arr[1];
                console.log(new_uid);
                // this.updateGroup(family);
            })
        });
    }

    getFamilies(){
        return new Promise((resolve, reject)=>{
            const config = {
                headers:{
                    Authorization: 'Bearer '+localStorage.getItem('jwt')
                }
            };
            axios.get('keluargas?uid_contains=001-002-0021', config)
            .then(res => {
                resolve(res.data);
            })
            .catch(err => {
                reject(err);
                console.error('There was an error!', err);
            });
        }) 
    }

    updateGroup(keluarga){

        console.log(keluarga.uid);
        keluarga.family_members.map((family_member, i)=>{
            let index_plus = i+1;
            let index_new;
            if(this.countDigit(index_plus) == 1){
                index_new = "00"+index_plus;
            }else if(this.countDigit(index_plus) == 2){
                index_new = "0"+index_plus;
            }else if(this.countDigit(index_plus) == 3){
                index_new = ""+index_plus;
            }
            family_member.uid = keluarga.uid+"-"+index_new;

            const headers = { 
                'Content-Type': 'application/json',
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            };

            let data = family_member;
            axios.put('/family-members/'+family_member.id, data, {headers})
            .then(response => {
                console.log("Update Berhasil");
            })
            .catch(error => {
                alert("Update Gagal !, "+error);
            });

        })

        const headers = { 
            'Content-Type': 'application/json',
            Authorization: 'Bearer '+localStorage.getItem('jwt')
        };

        let data = keluarga;
        data.is_update_anggota_uid = 1;
        axios.put('/keluargas/'+keluarga.id, data, {headers})
        .then(response => {
            console.log("Update Berhasil");
        })
        .catch(error => {
            alert("Update Gagal !, "+error);
        });
    }

    countDigit(number){
        let string_number = number.toString();
        let number_arr = string_number.split();
        return number_arr.length;
    }






    render(){
        return(
            <h1>UpdateGroup</h1>
        );
    }

}

export default withRouter(UpdateGroup);
