                id: this.props.match.params.param_2,
                page: <Index/>
            });
        }

    }



    render(){
        return(
            <Container fluid className="main-content-container px-4 pb-4">
                <Col lg="12">
                    {this.state.page}
                </Col>
            </Container>
        );
    }

}

export default withRouter(AnggotaKeluarga);
