import React from 'react';
import * as XLSX from 'xlsx';
import axios from 'axios';

const ImportSheet =  () => {

    const readExcel = (file) => {
        const promise = new Promise((resolve, reject)=>{
            const fileReader = new FileReader();
            fileReader.readAsArrayBuffer(file);

            fileReader.onload = (e)=>{
                const bufferArray = e.target.result;

                const workBook = XLSX.read(bufferArray, {type:'buffer'});
                const workSheetName = workBook.SheetNames[1];
                const workSheet = workBook.Sheets[workSheetName];

                const data = XLSX.utils.sheet_to_json(workSheet);
                resolve(data);
            }

        });

        const url = 'http://localhost:1337/';
        var currentdate = new Date();
        var datetime = "Last Sync: " + currentdate.getDate() + "/"+(currentdate.getMonth()+1) 
        + "/" + currentdate.getFullYear() + " @ " 
        + currentdate.getHours() + ":" 
        + currentdate.getMinutes() + ":" + currentdate.getSeconds();

        const log_data = {
            file_name: datetime
        }

        promise.then((datas)=>{
            // axios.post(url+"import-logs", log_data)
            // .then(response => {
            //     var import_log_id = response.data.id 
            //     datas.forEach(data => {

            //     });
            // })

            datas.forEach(data => {
                axios.get(url+'groups?on_sheet_id='+data['KBG']).then(resp => {
                    console.log(resp.data);
                });
                // console.log(data);
                // const restructure_data = {
                //     on_sheet_no_kk: data['No KK'],
                //     nama: data['Nama KK'],
                //     nama_panggilan: data['Nama Panggilan'],
                //     alamat_1: data['Alamat1'],
                //     alamat_2: data['Alamat2'],
                //     kode_pos: data['Kode Pos'],
                //     telepon: data['Telp Rumah'],
                //     impor_log: null
                // }
            });

        })

    }

    return(
        <div class="flex w-full h-screen items-center justify-center bg-grey-lighter">
            <label class="w-64 flex flex-col items-center px-4 py-6 bg-white text-blue rounded-lg shadow-lg tracking-wide uppercase border border-blue cursor-pointer hover:bg-blue hover:text-white">
                <svg class="w-8 h-8" fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                    <path d="M16.88 9.1A4 4 0 0 1 16 17H5a5 5 0 0 1-1-9.9V7a3 3 0 0 1 4.52-2.59A4.98 4.98 0 0 1 17 8c0 .38-.04.74-.12 1.1zM11 11h3l-4-4-4 4h3v3h2v-3z" />
                </svg>
                <span class="mt-2 text-base leading-normal">Select a file</span>
                <input type='file' class="hidden" onChange={(e)=>{
                    const file = e.target.files[0];
                    readExcel(file);
                }} />
            </label>
        </div>
    )

}

export default ImportSheet
