import React from "react";
import axios from "axios";
import { Container, Col } from "shards-react";
import { withRouter } from "react-router";
import KeluargaIndex from "../components/keluargas/index";
import KeluargaShow from "../components/keluargas/show";

class Keluarga extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: null,
            page: null,
            data_keluargas: null
        }
    }

    componentDidMount() {
        this.setState({
            id: this.props.match.params.id
        });

        let page;
        if(this.props.match.params.id != null){
            page = <KeluargaShow id={this.state.id}/>;
        }else{

            const config = {
                headers:{
                    Authorization: 'Bearer '+localStorage.getItem('jwt')
                }
            };

            axios.get('keluargas', config)
            .then(res => {
                console.log(res);
                this.setState({
                    datas: res.data
                });


            })
            .catch(err => {
                console.error('There was an error!', err);
            });

            page = <KeluargaIndex/>;
        }

        this.setState({
            page: page
        });
    }

    render(){
        // let page = this.state.page;
        let page;
        if(this.props.match.params.id != null){
            page = <KeluargaShow id={this.state.id}/>;
        }else{
            page = <KeluargaIndex/>;
        }

        return(
            <Container fluid className="main-content-container px-4 pb-4">
                <Col lg="12">
                    {page}
                </Col>
            </Container>
        );
    }

}

export default withRouter(Keluarga);
