import React from "react";
import axios from "axios";
import PropTypes from "prop-types";
import PageTitle from "../common/PageTitle";
import {
    Col,
    Card,
    CardHeader,
    CardBody,
    Container,
    Row,
} from "shards-react";




class Login extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            identifier: "",
            password: "",
            user: ""
        }

        this.user = {
            jwt: "",
            user: {

            }
        }
    }

    login(data){
        const headers = { 
            'Content-Type': 'application/json',
        };

        axios.post('auth/local', data, {headers})
        .then(response => {
            alert("Login Berhasil");

            localStorage.setItem('jwt', response.data.jwt)

            window.location.href = "/admin/keluarga";
        })
        .catch(error => {
            this.setState({ errorMessage: error.message });
            console.error('There was an error!', error.message);
            alert("Login Gagal !, "+error);
        });
    }

    mySubmitHandler = (event) => {
        this.login(this.state);
    }

    handleInputChanged(event) {
        this.setState({
            [event.target.name] : event.target.value,
        });
    }

    cekState(event){
        console.log( localStorage.getItem('jwt') )
    }


    render() {
        return (
            <Container fluid className="main-content-container px-4 pb-4">
                <Row noGutters className="page-header py-4">
                    <PageTitle sm="4" title="Keluarga" subtitle="Data Keluarga" className="text-sm-left" />
                </Row>
                <Row>
                {/* Editor */}
                    <Col lg="9" md="12">
                        <Card small className="mb-4">
                            <CardHeader className="border-bottom">
                                <h6 className="m-0">Data Keluarga</h6>
                            </CardHeader>
                            <CardBody className="p-0 pb-3">
                                <table className="table mb-0">
                                    <thead className="bg-light">
                                        <tr>
                                        <th scope="col" className="border-0">
                                            Status
                                        </th>
                                        <th scope="col" className="border-0">
                                            Nama Panggilan
                                        </th>
                                        <th scope="col" className="border-0">
                                            Nama Lengkap
                                        </th>
                                        <th scope="col" className="border-0">
                                            Agama
                                        </th>
                                        <th scope="col" className="border-0">
                                            City
                                        </th>
                                        <th scope="col" className="border-0">
                                            Phone
                                        </th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                        <td>1</td>
                                        <td>Ali</td>
                                        <td>Kerry</td>
                                        <td>Russian Federation</td>
                                        <td>Gda≈Ñsk</td>
                                        <td>107-0339</td>
                                        </tr>
                                        <tr>
                                        <td>2</td>
                                        <td>Clark</td>
                                        <td>Angela</td>
                                        <td>Estonia</td>
                                        <td>Borghetto di Vara</td>
                                        <td>1-660-850-1647</td>
                                        </tr>
                                        <tr>
                                        <td>3</td>
                                        <td>Jerry</td>
                                        <td>Nathan</td>
                                        <td>Cyprus</td>
                                        <td>Braunau am Inn</td>
                                        <td>214-4225</td>
                                        </tr>
                                        <tr>
                                        <td>4</td>
                                        <td>Colt</td>
                                        <td>Angela</td>
                                        <td>Liberia</td>
                                        <td>Bad Hersfeld</td>
                                        <td>1-848-473-7416</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </CardBody>
                        </Card>
                    </Col>

                    {/* Sidebar Widgets */}
                    <Col lg="3" md="12">
                        <Card small className="mb-4">
                            <CardHeader className="border-bottom">
                                <h6 className="m-0">Group</h6>
                            </CardHeader>
                            <CardBody className="p-0 pb-3">

                            </CardBody>
                        </Card>
                    </Col>
                </Row>

            </Container>


        )
    }
}

export default Login;
