import React from "react";
import { Container, Col } from "shards-react";
import { withRouter } from "react-router";
import Keluargas from "../components/keluargas/index";

class Keluarga extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

            datas: [],
            columns: [
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Panggilan',
                    selector: 'panggilan',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Alamat',
                    selector: 'alamat',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Telepon',
                    selector: 'telepon',
                    sortable: true,
                    right: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button
                                    className="btn btn-primary btn-sm"
                                    onClick={() => this.editRecord(record)}
                                >
                                    <i className="fa fa-edit"></i>
                                </button>
                                <button 
                                    className="btn btn-danger btn-sm" 
                                    onClick={() => this.deleteRecord(record)}
                                >
                                    <i className="fa fa-trash"></i>
                                </button>
                            </Fragment>
                        );
                    }
                }
            ]
        }
    }

    componentDidMount() {
        const id = this.props.match.params.id;
        this.fetchData(id);
    }

    fetchData = id => {
        // ...
    };

    render(){
        return(
            <Container fluid className="main-content-container px-4 pb-4">
                <Col lg="12">
                    <Keluargas />
                </Col>
            </Container>
        );
    }

}

export default Keluarga;
