import React, { useState, useEffect, useCallback, useMemo, Fragment } from "react";
import axios from "axios";
import PropTypes from "prop-types";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import {
    Col,
    Row,
    Container,
    Card,
    CardHeader,
    CardBody,
    Button,
    ButtonGroup
} from "shards-react";

const data = [{ id: 1, nama: 'Ali', panggilan: 'Kerry', alamat: 'Bali', telepon: '081353358388', action: '' }];
const columns = [
];





class Index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            datas: [{ id: 1, nama: 'Ali', panggilan: 'Kerry', alamat: 'Bali', telepon: '081353358388', action: '' }]
        }


    }

    editRecord(record) {
        console.log("Edit Record", record);
    }

    deleteRecord(record) {
        console.log("Delete Record", record);
    }

    componentDidMount(){
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };

        axios.get('keluargas', config)
        .then(res => {
            console.log(res);
            this.setState({
                datas: res.data
            });
        })
        .catch(err => {
            console.error('There was an error!', err);
        });


    }

    tesData(){
        console.log(this.tes);
    }

    render(){

        return(
            <Container fluid className="main-content-container px-4 pb-4">
                <Row noGutters className="page-header py-4">
                    <PageTitle sm="4" title="Keluarga" subtitle="Data Keluarga" className="text-sm-left" />
                </Row>
                <Button onClick={this.tesData}>Tes</Button>
                <Row>
                    <Col>
                        <Card small className="mb-4">
                        <CardHeader className="border-bottom">

                        </CardHeader>
                        <CardBody className="p-0 pb-3">
                            <table className="table mb-0">
                                <thead className="bg-light">
                                    <tr>
                                    <th scope="col" className="border-0">
                                        #
                                    </th>
                                    <th scope="col" className="border-0">
                                        Nama
                                    </th>
                                    <th scope="col" className="border-0">
                                        Panggilan
                                    </th>
                                    <th scope="col" className="border-0">
                                        Alamat
                                    </th>
                                    <th scope="col" className="border-0">
                                        Telepon
                                    </th>
                                    <th scope="col" className="border-0">
                                        Action
                                    </th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                    <td>1</td>
                                    <td>Ali</td>
                                    <td>Kerry</td>
                                    <td>Russian Federation</td>
                                    <td>Gda≈Ñsk</td>
                                    <td>
                                    <ButtonGroup className="mb-0">
                                        <Button theme="primary">Edit</Button>
                                        <Button theme="danger">Delete</Button>
                                    </ButtonGroup>
                                    </td>
                                    </tr>
                                    <tr>
                                    <td>2</td>
                                    <td>Clark</td>
                                    <td>Angela</td>
                                    <td>Estonia</td>
                                    <td>Borghetto di Vara</td>
                                    <td>
                                    <ButtonGroup className="mb-0">
                                        <Button theme="primary">Edit</Button>
                                        <Button theme="danger">Delete</Button>
                                    </ButtonGroup>
                                    </td>
                                    </tr>
                                    <tr>
                                    <td>3</td>
                                    <td>Jerry</td>
                                    <td>Nathan</td>
                                    <td>Cyprus</td>
                                    <td>Braunau am Inn</td>
                                    <td>
                                    <ButtonGroup className="mb-0">
                                        <Button theme="primary">Edit</Button>
                                        <Button theme="danger">Delete</Button>
                                    </ButtonGroup>
                                    </td>
                                    </tr>
                                    <tr>
                                    <td>4</td>
                                    <td>Colt</td>
                                    <td>Angela</td>
                                    <td>Liberia</td>
                                    <td>Bad Hersfeld</td>
                                    <td>
                                    <ButtonGroup className="mb-0">
                                        <Button theme="primary">Edit</Button>
                                        <Button theme="danger">Delete</Button>
                                    </ButtonGroup>
                                    </td>
                                    </tr>
                                </tbody>
                            </table>
                        </CardBody>
                        </Card>
                    </Col>
                </Row>


                <Row>
                    <Col>
                        <Card small className="mb-4">

                            <CardBody className="p-0 pb-3">
                                <DataTable
                                    columns={columns}
                                    data={this.state.datas}
                                    pagination
                                    sortIcon
                                />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>


            </Container>



        );
    }


}

export default Index;
