import React, {Fragment} from "react";
import axios from "axios";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import {
    Col,
    Card,
    CardHeader,
    CardBody,
    CardFooter,
    Container,
    Row,
    ListGroup,
    ListGroupItem,
    Button,
    Form,
    FormInput,
    FormSelect,
    FormGroup,
    FormTextarea,
} from "shards-react";




class Show extends React.Component {
    constructor(props) {
        super(props);
        // Bind Functions
        this.addRecord = this.addRecord.bind(this);
        this.changeGroup = this.changeGroup.bind(this);

        this.state = {
            id: null,
            anggota_keluarga: [],
            group: {},
            columns: [

                {
                    name: 'Status',
                    selector: 'relasi_family',
                    sortable: true,
                },
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Nama Panggilan',
                    selector: 'nama_panggilan',
                    sortable: true,
                },
                {
                    name: 'Gender',
                    selector: 'gender',
                    sortable: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button 
                                    className="ml-auto btn btn-outline-accent btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    Detail
                                </button>
                            </Fragment>
                        );
                    }
                }
            ]
        }



    }

    showRecord(record){
        window.location.href = "/admin/anggota-keluarga/show/"+record.id;
    }

    addRecord(){
        window.location.href = "/admin/keluarga/tambah-anggota/"+this.state.id;
    }

    changeGroup(){
        window.location.href = "/admin/keluarga/edit-group/"+this.state.id;
    }



    componentDidMount() {
        console.log('Component /anggota-keluargas/show.js | Function componentDidMount');
        this.getAnggotaKeluarga(this.props.id);
    }

    getAnggotaKeluarga(id){
        console.log('Component /anggota-keluargas/show.js | Function getAnggotaKeluarga');
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };
        axios.get('family-members?id='+id, config)
        .then(res => {
            // Setiap SetState Akan Menjalankan Ulang Fungsi Render
            console.log(res);
            this.setState({
                id: id,
                anggota_keluarga: res.data[0].family_members,
                group: res.data[0].group
            });
        })
        .catch(err => {
            console.error('There was an error!', err);
        });
    }

    handleClick(id){
        console.log("edit "+id);
    }   


    render() {
        console.log('Component /anggota-keluargas/show.js | Function render');
        let anggota_keluarga = [];
        if(this.state.anggota_keluarga != null && Array.isArray(this.state.anggota_keluarga)){
            anggota_keluarga = this.state.anggota_keluarga;
        }

        return (
            <Container fluid className="main-content-container px-4 pb-4">

            </Container>


        )
    }
}

export default Show;
