import React from "react";
import axios from "axios";
import { Container, Col } from "shards-react";
import { withRouter } from "react-router";
import Index from "../components/keluargas/index";
import Show from "../components/keluargas/show";

class Keluarga extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: 0,
            page: null,
            data_keluargas: null
        }
    }

    componentDidMount() {
        let url = window.location.href;
        let url_splitted =  url.split('/admin/keluarga/');
        let page;
        if(this.props.match.params.param_1 == 'show'){
            console.log('param 1 '+this.props.match.params.param_1 );
            console.log(window.location.href);
            this.setState({
                id: this.props.match.params.param_2,
                page: <Show id={this.state.id}/>
            });

        }else{
            // this.getDataAll();
            // let data = this.state.data;
            console.log('param 1 '+this.props.match.params.param_1 );
            console.log(window.location.href);

            this.setState({
                id: this.props.match.params.param_2,
                page: <Index/>
            });
        }

    }

    getDataAll(){
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };

        axios.get('keluargas', config)
        .then(res => {
            console.log("Di Keluarga");

            console.log(res);
            this.setState({
                datas: res.data
            });
        })
        .catch(err => {
            console.error('There was an error!', err);
        });
    }

    render(){
        return(
            <Container fluid className="main-content-container px-4 pb-4">
                <Col lg="12">
                    {this.state.page}
                </Col>
            </Container>
        );
    }

}

export default withRouter(Keluarga);
