import React from "react";
import { Container, Col } from "shards-react";
import { withRouter } from "react-router";
import KeluargaIndex from "../components/keluargas/index";
import KeluargaForm from "../components/keluargas/form";
import UserLogin from "../components/auth/login";

class Keluarga extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: null,
        }
    }

    componentDidMount() {
        this.setState({
            id: this.props.match.params.id
        });
    }

    render(){
        let page;
        if(this.state.id != null){
            page = <UserLogin/>;
        }else{
            page = <KeluargaIndex/>;
        }

        return(
            <Container fluid className="main-content-container px-4 pb-4">
                <Col lg="12">
                    {page}
                </Col>
            </Container>
        );
    }

}

export default withRouter(Keluarga);
