import React, { useState, useEffect, useCallback, useMemo, Fragment } from "react";
import {useParams } from "react-router-dom";
import axios from "axios";
import PropTypes from "prop-types";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import Select from "../partials/select";
import {
    Col,
    Row,
    Container,
    Card,
    CardHeader,
    CardBody,
    Button,
    FormInput
} from "shards-react";


class Index extends React.Component {


    constructor(props) {
        super(props);
        this.state = {

            datas: [],
            katedral: [],
            columns: [
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Panggilan',
                    selector: 'panggilan',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Alamat',
                    selector: 'alamat',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Telepon',
                    selector: 'telepon',
                    sortable: true,
                    right: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button
                                    className="btn btn-primary btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    {/* <i className="fa fa-edit"></i>  */}
                                    Detail
                                </button>
                                {/* <button 
                                    className="btn btn-danger btn-sm" 
                                    onClick={() => this.deleteRecord(record)}
                                >
                                    <i className="fa fa-trash"></i>
                                </button> */}
                            </Fragment>
                        );
                    }
                }
            ]
        }
    }


    showRecord(record) {
        console.log("Edit Record", record);
        window.location.href = "/admin/keluarga/show/"+record.id;
    }

    deleteRecord(record) {
        console.log("Delete Record", record);
        // window.location.href = "/admin/keluarga";
    }

    componentDidMount(){
        this.getKatedral();
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };

        axios.get('keluargas', config)
        .then(res => {
            console.log("Di Index");

            console.log(res);
            this.setState({
                datas: res.data
            });


        })
        .catch(err => {
            console.error('There was an error!', err);
        });

    }

    getKatedral(){
        console.log('Component /keluargas/index.js | Function getKatedral');
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };

        axios.get('groups?group_category=1', config)
        .then(res => {

            console.log(res);
            this.setState({
                katedral: res.data
            });


        })
        .catch(err => {
            console.error('There was an error!', err);
        });
    }

    tesData(){
        console.log(this.tes);
    }

    render(){
        console.log('Component /keluargas/index.js | Function render');
        console.log(this.state.katedral);
        let data_gender = [
            {
                value: "",
                text: "-"
            },
            {
                value: "lajang",
                text: "Lajang"
            },
            {
                value: "menikah",
                text: "Menikah"
            },
            {
                value: "cerai",
                text: "Cerai"
            },
        ];

        let katedral = this.state.katedral;
        for(let i=0; i<katedral; i++){
            katedral[i].value = katedral[i].id;
            katedral[i].text = katedral[i].name;
            katedral[i].name = 'cuk';
        }


        // katedral[0]['name'] = 'cuk';
        console.log(katedral);
        return(

            <Container fluid className="main-content-container px-4 pb-4">
                <Row noGutters className="page-header py-4">
                    <PageTitle sm="1" title="Keluarga" subtitle="Data Keluarga" className="text-sm-left" />

                </Row>


                <Row>
                    <Col>
                        <Card small className="mb-4">
                            <CardHeader>

                                <Row form>
                                    <Col md="3" className="form-group">
                                        <label htmlFor="feFilterKatedral"><b>Katedral</b></label>
                                        <Select id="feFilterKatedral" 
                                            data={katedral}   
                                            name="filter_katedral"

                                            // onChange={this.handleInputChanged.bind(this)}
                                        />
                                    </Col>
                                    <Col md="3" className="form-group">
                                        <label htmlFor="feFilterKatedral"><b>Paroki</b></label>
                                        <Select id="feFilterKatedral" 
                                            data={data_gender}   
                                            name="filter_katedral"
                                            // onChange={this.handleInputChanged.bind(this)}
                                        />
                                    </Col>
                                    <Col md="3" className="form-group">
                                        <label htmlFor="feFilterKatedral"><b>Lingkungan</b></label>
                                        <Select id="feFilterKatedral" 
                                            data={data_gender}   
                                            name="filter_katedral"
                                            // onChange={this.handleInputChanged.bind(this)}
                                        />
                                    </Col>
                                    <Col md="3" className="form-group">
                                        <label htmlFor="feFilterKatedral"><b>KBG</b></label>
                                        <Select id="feFilterKatedral" 
                                            data={data_gender}   
                                            name="filter_katedral"
                                            // onChange={this.handleInputChanged.bind(this)}
                                        />
                                    </Col>
                                </Row>
                                <Row form>
                                    <Col md="12" className="form-group">
                                        <label htmlFor="feFilterSearch"><b>Search</b></label>
                                        <FormInput
                                            id="feFilterSearch"
                                            placeholder="Search"
                                            name="filter_search"
                                        />
                                    </Col>

                                </Row>
                                <Button onClick={this.tesData} className=""><i className="fa fa-plus"></i> Add</Button>&nbsp;
                                <Button outline onClick={this.tesData} className="">Filter</Button>
                            </CardHeader>
                            <CardBody className="p-0 pb-3 px-3 py-3">

                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.datas}
                                    pagination
                                    sortIcon
                                />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>


            </Container>



        );
    }


}

export default Index;
