import React, {Fragment} from "react";
import axios from "axios";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import {
    Col,
    Card,
    CardHeader,
    CardBody,
    CardFooter,
    Container,
    Row,
    ListGroup,
    ListGroupItem,
    Button,
    Form,
    FormInput,
    FormSelect,
    FormGroup,
    FormTextarea,
} from "shards-react";




class Show extends React.Component {
    constructor(props) {
        super(props);
        // Bind Functions
        this.addRecord = this.addRecord.bind(this);
        this.changeGroup = this.changeGroup.bind(this);

        this.state = {
            id: null,
            anggota_keluarga: [],
            group: {},
            columns: [

                {
                    name: 'Status',
                    selector: 'relasi_family',
                    sortable: true,
                },
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Nama Panggilan',
                    selector: 'nama_panggilan',
                    sortable: true,
                },
                {
                    name: 'Gender',
                    selector: 'gender',
                    sortable: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button 
                                    className="ml-auto btn btn-outline-accent btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    Detail
                                </button>
                            </Fragment>
                        );
                    }
                }
            ]
        }



    }

    showRecord(record){
        window.location.href = "/admin/anggota-keluarga/show/"+record.id;
    }

    addRecord(){
        window.location.href = "/admin/keluarga/tambah-anggota/"+this.state.id;
    }

    changeGroup(){
        window.location.href = "/admin/keluarga/edit-group/"+this.state.id;
    }



    componentDidMount() {
        console.log('Component /anggota-keluargas/show.js | Function componentDidMount');
        this.getAnggotaKeluarga(this.props.id);
    }

    getAnggotaKeluarga(id){
        console.log('Component /anggota-keluargas/show.js | Function getAnggotaKeluarga');
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };
        axios.get('family-members?id='+id, config)
        .then(res => {
            // Setiap SetState Akan Menjalankan Ulang Fungsi Render
            console.log(res);
            this.setState({
                id: id,
                anggota_keluarga: res.data[0],
            });
        })
        .catch(err => {
            console.error('There was an error!', err);
        });
    }

    handleClick(id){
        console.log("edit "+id);
    }   


    render() {
        console.log('Component /anggota-keluargas/show.js | Function render');
        let anggota_keluarga = [];
        if(this.state.anggota_keluarga != null && Array.isArray(this.state.anggota_keluarga)){
            anggota_keluarga = this.state.anggota_keluarga;
        }

        return (
            <Container fluid className="main-content-container px-4 pb-4">
                <Row noGutters className="page-header py-4">
                    <PageTitle sm="4" title="Keluarga" subtitle="Anggota Keluarga" className="text-sm-left" />
                </Row>
                <Row>
                {/* Editor */}
                    <Col lg="12" md="12">
                        <Card small className="mb-4 overflow-hidden">
                            <Form>
                            <CardHeader className="border-bottom">
                                <h6 className="">Data Anggota Keluarga&nbsp;

                                </h6>
                            </CardHeader>
                            <CardBody className="p-0 pb-3">
                            <ListGroup flush>
                                <ListGroupItem className="p-3">
                                    <Row>
                                    <Col>
                                        <Row   Row form>
                                            {/* Last Name */}
                                            <Col md="12" className="form-group">
                                            <label htmlFor="feNama">Nama</label>
                                            <FormInput
                                                id="feNama"
                                                placeholder="Nama"
                                                value={this.state.anggota_keluarga.nama}
                                                onChange={() => {}}
                                            />
                                            </Col>
                                        </Row>
                                        <Row form>
                                            {/* First Name */}
                                            <Col md="12" className="form-group">
                                            <label htmlFor="fePanggilan">Panggilan</label>
                                            <FormInput
                                                id="fePanggilan"
                                                placeholder="Panggilan"
                                                value={this.state.anggota_keluarga.nama_panggilan}
                                                onChange={() => {}}
                                            />
                                            </Col>
                                        </Row>

                                        <Row form>
                                            {/* Email */}
                                            <Col md="6" className="form-group">
                                            <label htmlFor="feRelasiFamily">Status Dalam Keluarga</label>
                                            <FormInput
                                                id="feRelasiFamily"
                                                placeholder="Status Dalam Keluarga "
                                                value={this.state.anggota_keluarga.relasi_family}
                                                onChange={() => {}}
                                                autoComplete="email"
                                            />
                                            </Col>
                                            {/* Password */}
                                            <Col md="6" className="form-group">
                                            <label htmlFor="feGender">Gender</label>
                                            <FormSelect id="feGender">
                                                <option>-</option>
                                                <option>Pria</option>
                                                <option>Wanita</option>
                                            </FormSelect>
                                            </Col>
                                        </Row>
                                        <Row form>
                                            {/* Email */}
                                            <Col md="6" className="form-group">
                                            <label htmlFor="fePhone">Phone</label>
                                            <FormInput
                                                id="fePhone"
                                                placeholder="Phone"
                                                value={this.state.anggota_keluarga.phone}
                                                onChange={() => {}}

                                            />
                                            </Col>
                                            {/* Password */}
                                            <Col md="6" className="form-group">
                                            <label htmlFor="feEmail">Email</label>
                                            <FormInput
                                                id="feEmail"
                                                placeholder="Email"
                                                value={this.state.anggota_keluarga.email}
                                                onChange={() => {}}
                                            />
                                            </Col>
                                        </Row>



                                    </Col>
                                    </Row>
                                </ListGroupItem>
                            </ListGroup>
                            </CardBody>
                            <CardFooter>
                                <Button outline theme="accent" size="sm" className="ml-auto" onClick={this.changeGroup}>
                                    <i className="fa fa-edit"></i> Ubah
                                </Button>
                            </CardFooter>
                            </Form>
                        </Card>
                    </Col>

                    {/* Sidebar Widgets */}
                    <Col lg="12" md="12">
                        <Card small className="mb-4">
                            <CardHeader className="border-bottom">
                                <h6 className="m-0">Sakramen&nbsp;&nbsp;
                                    <Button outline theme="accent" size="sm" className="ml-auto" onClick={this.addRecord}>
                                        <i className="fa fa-plus"></i> Tambah Sakramen
                                    </Button>
                                </h6>

                            </CardHeader>
                            <CardBody className="p-0 pb-3">

                            </CardBody>
                            <CardFooter>

                            </CardFooter>
                        </Card>
                    </Col>
                </Row>

            </Container>


        )
    }
}

export default Show;
