import React, { useState, useEffect, useCallback, useMemo, Fragment } from "react";
import {useParams } from "react-router-dom";
import axios from "axios";
import PropTypes from "prop-types";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import {
    Col,
    Row,
    Container,
    Card,
    CardHeader,
    CardBody,
    Button,
    ButtonGroup
} from "shards-react";

const data = [{ id: 1, nama: 'Ali', panggilan: 'Kerry', alamat: 'Bali', telepon: '081353358388', action: '' }];
const columns = [
    {
        name: 'Nama',
        selector: 'nama',
        sortable: true,
    },
    {
        name: 'Panggilan',
        selector: 'panggilan',
        sortable: true,
        right: true,
    },
    {
        name: 'Alamat',
        selector: 'alamat',
        sortable: true,
        right: true,
    },
    {
        name: 'Telepon',
        selector: 'telepon',
        sortable: true,
        right: true,
    },
    {
        key: "action",
        text: "Action",
        className: "action",
        width: 100,
        align: "left",
        sortable: false,
        cell: record => { 
            return (
                <Fragment>
                    <button
                        className="btn btn-primary btn-sm"
                        onClick={() => this.editRecord(record)}
                    >
                        <i className="fa fa-edit"></i>
                    </button>
                    <button 
                        className="btn btn-danger btn-sm" 
                        onClick={() => this.deleteRecord(record)}
                    >
                        <i className="fa fa-trash"></i>
                    </button>
                </Fragment>
            );
        }
    }
];




class Index extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            id: useParams(),
            datas: [],
            columns: [
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Panggilan',
                    selector: 'panggilan',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Alamat',
                    selector: 'alamat',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Telepon',
                    selector: 'telepon',
                    sortable: true,
                    right: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button
                                    className="btn btn-primary btn-sm"
                                    onClick={() => this.editRecord(record)}
                                >
                                    <i className="fa fa-edit"></i>
                                </button>
                                <button 
