import React, {Fragment} from "react";
import axios from "axios";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import {
    Col,
    Card,
    CardHeader,
    CardBody,
    CardFooter,
    Container,
    Row,
    ListGroup,
    ListGroupItem,
    Button,
} from "shards-react";




class Login extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id: null,
            anggota_keluarga: [],
            group: {},
            columns: [

                {
                    name: 'Status',
                    selector: 'relasi_family',
                    sortable: true,
                },
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Nama Panggilan',
                    selector: 'nama_panggilan',
                    sortable: true,
                },
                {
                    name: 'Gender',
                    selector: 'gender',
                    sortable: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button
                                    className="btn btn-primary btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    Detail
                                </button>
                            </Fragment>
                        );
                    }
                }
            ]
        }



    }

    showRecord(record){
        window.location.href = "/admin/anggota-keluarga/show/"+record.id;
    }

    componentDidMount() {
        console.log('Component /Keluargas/show.js | Function componentDidMount');
        this.getAnggotaKeluarga(this.props.id);
    }

    getAnggotaKeluarga(id){
        console.log('Component /Keluargas/show.js | Function getAnggotaKeluarga');
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };
        axios.get('keluargas?id='+id, config)
        .then(res => {
            // Setiap SetState Akan Menjalankan Ulang Fungsi Render
            console.log(res);
            this.setState({
                anggota_keluarga: res.data[0].family_members,
                group: res.data[0].group
            });
        })
        .catch(err => {
            console.error('There was an error!', err);
        });
    }

    handleClick(id){
        console.log("edit "+id);
    }   


    render() {
        console.log('Component /Keluargas/show.js | Function render');
        let anggota_keluarga = [];
        if(this.state.anggota_keluarga != null && Array.isArray(this.state.anggota_keluarga)){
            anggota_keluarga = this.state.anggota_keluarga;
        }

        return (
            <Container fluid className="main-content-container px-4 pb-4">
                <Row noGutters className="page-header py-4">
                    <PageTitle sm="4" title="Keluarga" subtitle="Data Keluarga" className="text-sm-left" />
                </Row>
                <Row>
                {/* Editor */}
                    <Col lg="9" md="12">
                        <Card small className="mb-4 overflow-hidden">
                            <CardHeader className="border-bottom">
                                <h6 className="m-0">Data Keluarga</h6>
                            </CardHeader>
                            <CardBody className="p-0 pb-3">
                            <DataTable
                                    columns={this.state.columns}
                                    data={this.state.anggota_keluarga}
                                    // pagination
                                    // sortIcon
                                />
                                {/* <table className="table">
                                    <thead className="bg-light">
                                        <tr>
                                            <th scope="col" className="border-0">
                                                Status
                                            </th>
                                            <th scope="col" className="border-0">
                                                Nama Panggilan
                                            </th>
                                            <th scope="col" className="border-0">
                                                Nama Lengkap
                                            </th>
                                            <th scope="col" className="border-0">
                                                Agama
                                            </th>
                                            <th scope="col" className="border-0">
                                                Pendidikan
                                            </th>
                                            <th scope="col" className="border-0">
                                                Pekerjaan
                                            </th>
                                            <th scope="col" className="border-0">

                                            </th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {anggota_keluarga.map(function(keluarga, i){
                                            return (
                                                <tr className="">
                                                    <td>{keluarga.relasi_family}</td>
                                                    <td>{keluarga.nama_panggilan}</td>
                                                    <td>{keluarga.nama}</td>
                                                    <td>{keluarga.agama}</td>
                                                    <td></td>
                                                    <td></td>
                                                    <td>
                                                        <Button theme="accent" size="sm" className="ml-auto" >
                                                            <i className="fa fa-edit"></i> Ubah
                                                        </Button>
                                                    </td>
                                                </tr>
                                            )

                                        })}

                                    </tbody>
                                </table> */}
                            </CardBody>
                            <CardFooter>
                                <Button outline theme="accent" size="sm" className="ml-auto">
                                    <i className="fa fa-plus"></i> Tambah Anggota
                                </Button>
                            </CardFooter>
                        </Card>
                    </Col>

                    {/* Sidebar Widgets */}
                    <Col lg="3" md="12">
                        <Card small className="mb-4">
                            <CardHeader className="border-bottom">
                                <h6 className="m-0">Group</h6>

                            </CardHeader>
                            <CardBody className="p-0 pb-3">
                                <ListGroup flush>
                                    <ListGroupItem className="p-3">
                                    <span className="d-flex mb-2">
                                        <i className="material-icons mr-1">flag</i>
                                        <strong className="mr-1">Katedral : </strong>
                                        <strong >-</strong>
                                    </span>
                                    <span className="d-flex mb-2">
                                    <i className="material-icons mr-1">flag</i>
                                        <strong className="mr-1">Paroki : </strong>
                                        <strong >-</strong>
                                    </span>
                                    <span className="d-flex mb-2">
                                    <i className="material-icons mr-1">flag</i>
                                        <strong className="mr-1">Lingkungan : </strong>
                                        <strong >-</strong>
                                    </span>
                                    <span className="d-flex">
                                    <i className="material-icons mr-1">flag</i>
                                        <strong className="mr-1">KBG : {(this.state.group.hasOwnProperty('name')) ? this.state.group.name : ''}</strong>

                                    </span>
                                    </ListGroupItem>

                                </ListGroup>
                            </CardBody>
                            <CardFooter>
                                <Button outline theme="accent" size="sm" className="ml-auto">
                                    <i className="fa fa-edit"></i> Ubah
                                </Button>
                            </CardFooter>
                        </Card>
                    </Col>
                </Row>

            </Container>


        )
    }
}

export default Login;
