                    name: 'Alamat',
                    selector: 'alamat',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Telepon',
                    selector: 'telepon',
                    sortable: true,
                    right: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button
                                    className="btn btn-primary btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    {/* <i className="fa fa-edit"></i>  */}
                                    Detail
