import React, {Fragment} from "react";
import axios from "axios";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import Select from "../partials/select";
import {
    Col,
    Card,
    CardHeader,
    CardBody,
    CardFooter,
    Container,
    Row,
    ListGroup,
    ListGroupItem,
    Button,
    Form,
    FormInput,
    FormSelect,
    FormGroup,
    FormTextarea,
} from "shards-react";




class Show extends React.Component {
    constructor(props) {
        super(props);
        // Bind Functions
        this.updateRecord = this.updateRecord.bind(this);

        this.state = {
            id: null,
            anggota_keluarga: [],
            group: {},
            columns: [

                {
                    name: 'Status',
                    selector: 'relasi_family',
                    sortable: true,
                },
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Nama Panggilan',
                    selector: 'nama_panggilan',
                    sortable: true,
                },
                {
                    name: 'Gender',
                    selector: 'gender',
                    sortable: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button 
                                    className="ml-auto btn btn-outline-accent btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    Detail
                                </button>
                            </Fragment>
                        );
                    }
                }
            ]
        }



    }


    updateRecord(){
        console.log('Component /anggota-keluargas/show.js | Function updateRecord');

        const headers = { 
            'Content-Type': 'application/json',
        };

        axios.post('auth/local', data, {headers})
        .then(response => {
            alert("Login Berhasil");

            localStorage.setItem('jwt', response.data.jwt)

            window.location.href = "/admin/keluarga";
        })
        .catch(error => {
            this.setState({ errorMessage: error.message });
            console.error('There was an error!', error.message);
            alert("Login Gagal !, "+error);
        });
    }



    componentDidMount() {
        console.log('Component /anggota-keluargas/show.js | Function componentDidMount');
        this.getAnggotaKeluarga(this.props.id);
    }

    getAnggotaKeluarga(id){
        console.log('Component /anggota-keluargas/show.js | Function getAnggotaKeluarga');
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };
