import { withRouter } from "react-router";
import KeluargaIndex from "../components/keluargas/index";
import KeluargaShow from "../components/keluargas/show";

class Keluarga extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: null,
            page: null,
            data_keluargas: null
        }
    }

    componentDidMount() {
        this.setState({
            id: this.props.match.params.ida
        });

        console.log(this.props.match.params.param_1);
        let page;
        if(this.props.match.params.param_1 == 'show'){
            page = <KeluargaShow id={this.state.id}/>;
        }else{
            page = <KeluargaIndex/>;
        }

        // let page;
