import React, { useState, useEffect, useCallback, useMemo, Fragment } from "react";
import {useParams } from "react-router-dom";
import axios from "axios";
import PropTypes from "prop-types";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import Select from "../partials/select";
import {
    Col,
    Row,
    Container,
    Card,
    CardHeader,
    CardBody,
    Button,
    FormInput
} from "shards-react";


class Index extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            keluargas: [],
            keluarga: null,
            katedrals: [],
            katedral: null,
            parokis: [],
            paroki: null,
            lingkungans: [],
            lingkungan: null,
            kbgs: [],
            kbg: null,
            columns: [
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Panggilan',
                    selector: 'panggilan',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Alamat',
                    selector: 'alamat',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Telepon',
                    selector: 'telepon',
                    sortable: true,
                    right: true,
                },
                {
                    key: "action",
                    text: "Action",
                    className: "action",
                    width: 100,
                    align: "left",
                    sortable: false,
                    cell: record => { 
                        return (
                            <Fragment>
                                <button
                                    className="btn btn-primary btn-sm"
                                    onClick={() => this.showRecord(record)}
                                >
                                    {/* <i className="fa fa-edit"></i>  */}
                                    Detail
                                </button>
                                {/* <button 
                                    className="btn btn-danger btn-sm" 
                                    onClick={() => this.deleteRecord(record)}
                                >
                                    <i className="fa fa-trash"></i>
                                </button> */}
                            </Fragment>
                        );
                    }
                }
            ]
        }
    }


    showRecord(record) {
        console.log("Edit Record", record);
        window.location.href = "/admin/keluarga/show/"+record.id;
    }


    componentDidMount(){
        console.log('Component /keluargas/index.js | Function componentDidMount');

        this.getKatedrals();
        this.getKeluargas();

    }

    getKeluargas(){
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };
        axios.get('keluargas', config)
        .then(res => {
            this.setState({
                keluargas: res.data,
            });
        })
        .catch(err => {
            console.error('There was an error!', err);
        });

    }

    getKatedrals(){
        console.log('Component /keluargas/index.js | Function getKatedral');
        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };

        axios.get('groups?group_category=1', config)
        .then(res => {
            let kats = [
                {
                    value: 0,
                    text: 'Silahkan Pilih'
                }
            ]
            for(let i=0; i<res.data.length;i++){
                let kat = {
                    value: res.data[i].id,
                    text: res.data[i].name,
                }
                kats.push(kat);
            }
            this.setState({
                katedrals: kats
            });


        })
        .catch(err => {
            console.error('There was an error!', err);
        });
    }

    handleInputKatedralChanged(event) {
        console.log('Component /keluargas/index.js | Function handleInputKatedralChanged');
        console.log('event.target.name = '+event.target.name);
        console.log('event.target.value = '+event.target.value);
        if(event.target.value != 0){
            this.setState({
                katedral: event.target.value
            });
            this.getParokis(event.target.value);
        }

        if(event.target.value == 0){
            this.setState({
                parokis : [],
                paroki : null,
                lingkungans : [],
                lingkungan : null,
                kbgs : [],
                kbg : null,
            });
        }
    }

    getParokis(id){
        console.log('Component /keluargas/index.js | Function getParokis');

        const config = {
            headers:{
                Authorization: 'Bearer '+localStorage.getItem('jwt')
            }
        };

        axios.get('groups?parent.id='+id, config)
        .then(res => {
            console.log(res.data);

            let parokis = [
                {
                    value: 0,
                    text: 'Silahkan Pilih'
                }
            ]
