import React, { useState, useEffect, useCallback, useMemo, Fragment } from "react";
import {useParams } from "react-router-dom";
import axios from "axios";
import PropTypes from "prop-types";
import DataTable from 'react-data-table-component';
import PageTitle from "../common/PageTitle";
import Select from "../partials/select";
import {
    Col,
    Row,
    Container,
    Card,
    CardHeader,
    CardBody,
    Button,
    FormInput
} from "shards-react";


class Index extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            keluargas: [],
            keluarga: null,
            katedrals: [],
            katedral: null,
            parokis: [],
            paroki: null,
            lingkungans: [],
            lingkungan: null,
            kbgs: [],
            kbg: null,
            filter_search: null,
            columns: [
                {
                    name: 'Nama',
                    selector: 'nama',
                    sortable: true,
                },
                {
                    name: 'Panggilan',
                    selector: 'panggilan',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Alamat',
                    selector: 'alamat',
                    sortable: true,
                    right: true,
                },
                {
                    name: 'Telepon',
                    selector: 'telepon',
                    sortable: true,
                    right: true,
