import React from "react";
import axios from "axios";
import { Container, Col } from "shards-react";
import { withRouter } from "react-router";
import Index from "../components/keluargas/index";
import Show from "../components/keluargas/show";
import Form from "../components/anggota-keluarga/form";

class UpdateGroup extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: 0,
        }
    }

    componentDidMount() {
        this.getFamilies().then( (families) =>{
            console.log(families);
            families.map((family, i)=>{
                console.log(family);
                // this.updateGroup(family);
            })
        });
    }

    getFamilies(){
        return new Promise((resolve, reject)=>{
            const config = {
                headers:{
                    Authorization: 'Bearer '+localStorage.getItem('jwt')
                }
            };
            axios.get('keluargas?', config)
            .then(res => {
                resolve(res.data);
            })
            .catch(err => {
                reject(err);
                console.error('There was an error!', err);
            });
        }) 
    }

    updateGroup(anggota_keluarga){
        anggota_keluarga.group = anggota_keluarga.family.group;
        anggota_keluarga.is_sudah_cek_group = 1;
        console.log(anggota_keluarga);

        const headers = { 
            'Content-Type': 'application/json',
            Authorization: 'Bearer '+localStorage.getItem('jwt')
        };
        let data = anggota_keluarga;
        data.is_sudah_cek_kk = 1;
        axios.put('/family-members/'+anggota_keluarga.id, data, {headers})
        .then(response => {
            console.log("Update Berhasil");
        })
        .catch(error => {
            alert("Update Gagal !, "+error);
        });
    }






    render(){
        return(
            <h1>UpdateGroup</h1>
        );
    }

}

export default withRouter(UpdateGroup);
